import React from 'react';
import './slideShow.style.css'

class Slider extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			prevDisable: true,
			nextDisable:  this.offsetWidth >= this.scrollWidth ? true : false
		};
	}

 componentDidMount() {
  this.checkButtons(this.offsetWidth, this.scrollWidth);
 }
	checkButtons = (offsetWidthValue, scrollWidthValue) => {
		this.setState({
			prevDisable: this.scrollLeft <= 0 ? true : false,
			nextDisable: this.scrollLeft + offsetWidthValue >= scrollWidthValue ? true : false
		});
	};

	render() {
		const offsetWidthValue = this.offsetWidth,
			scrollWidthValue = this.scrollWidth;
		return (
			<div
				className='slider-container'
			>
				<div className='slider-wrapper'>{this.props.children}</div>
				<div
					className={`btn prev ${this.state.prevDisable ? 'disable' : ''}`}
					disabled={this.state.prevDisable}
					onClick={() => {
						this.scrollLeft -= offsetWidthValue / 2;
						this.checkButtons(offsetWidthValue, scrollWidthValue);
					}}
				>
					{'<'}
				</div>
				<div
					className={`btn next ${this.state.nextDisable ? 'disable' : ''}`}
					disabled={this.state.nextDisable}
					onClick={() => {
						this.scrollLeft += offsetWidthValue / 2;
						this.checkButtons(offsetWidthValue, scrollWidthValue);
					}}
				>
					{'>'}
				</div>
			</div>
		);
	}
}

export default Slider;
