import React, {Component} from 'react';
import ProductList from './product-list';
import Card from '../../HOMEPAGE/mus-have-card/mus-have-card.component';
import './must-have.style.css';

class MustHave extends Component {
    constructor(props){
        super(props);
        this.state = {
            properties: ProductList.properties,
            property: ProductList.properties[0]
        }
    }

    nextProperty = () => {
        const newIndex = this.state.property.index+1;
        this.setState(
            { property: ProductList.properties[newIndex] }
        );
    }

    prevProperty = () => {
        const newIndex = this.state.property.index-1;
        this.setState(
            { property: ProductList.properties[newIndex] }
        );
    }

    render(){
        const {properties,property} = this.state;
        return(
            <div className="mustHavePage">
                <div className="products">
                    <Card property={property}/>
                </div>

                <div className="btn-container">
                    <div className="prev-next-button previous"
                    onClick = {()=> this.prevProperty()}
                    disabled = {property.index === 0}
                    >
                    <path class="arrow" 
                    d="M 50,0 L 60,10 L 20,50 L 60,90 L 50,100 L 0,50 Z" 
                    transform=" translate(15,0)" 
                    />
                    </div>

                    <div className="prev-next-button next" 
                    onClick = {()=> this.nextProperty()}
                    disabled = {property.index === ProductList.properties.length-1}
                    >
                        <path class="arrow" 
                        d="M 50,0 L 60,10 L 20,50 L 60,90 L 50,100 L 0,50 Z "
                        transform="translate(85,100) rotate(180) "
                        />
                    </div>
                </div>
            </div>

        );
    }
}

export default MustHave;
