import React from 'react';
import {connect} from 'react-redux';
import {addItem} from '../../redux/cart/cart.action';
import './skincare-card.style.css';



const SkincareCard = ({id, name, price, imageUrl, addItem}) => {



    return(
        <div className="skincare-card">
            <div className="skincare-content">
                <div className="skincareProductImage">
                    <img src={imageUrl} alt=''/>
                </div>
                <div className="skincareProductText">
                    <div className="ProductName">{name}</div>
                    <div className="ProductPrice">$ {price}</div>
                </div>
            </div>

            <button className="skincare-custom-btn" onClick = { () => addItem(item) }>
                Add to Cart
            </button>
        </div>
    );
}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})


export default connect(null, mapDispatchToProps)(SkincareCard);
